import toast from 'react-hot-toast'
import { authenticate } from './helper';



/** validate login page username */
export async function usernameValidate(values){

    const errors = usernameVerify({},values);


    if(values.username){
        //check user exist or not
        const {status} = await authenticate(values.username)

        if(status !==200){
            error.exit = toast
        }

    }

    return errors;

}
/**  validate password */

export async function passwordValidate(values){

    const errors = passwordVerify({},values);

    return errors;
}


/** validate password */
function passwordVerify(errors = {}, values){
    /* eslint-disable no-useless-escape */
    const specialChars = /[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;

    if(!values.password){
        errors.password = toast.error("Password Required...!");

    } else if(values.password.includes(" ")){
        errors.password = toast.error("Wrong Password...!");

    }else if(values.password.length < 4){
        errors.password = toast.error("Password must be more than 4 characters long");

    }else if(!specialChars.test(values.password)){
        errors.password = toast.error("Password must have special character");
    }

    return errors;
}

/** validate reset for password */
export async function resetPasswordValidation(values){

    const errors = passwordVerify({},values);

    if(values.password!== values.confirm_pwd){
        errors.exit = toast.error("password not match")
    }

    return errors;
}



/**  validate for resetPassword */




/** validate username */

function usernameVerify(error = [], values){

    if(!values.username){

        error.username = toast.error('Username Required...!');

    }else if(values.username.includes(" ")){

        error.username = toast.error('Invalid Username..!')
    }

    return error
}





/**  validation for register form */

export default function registerValidation(values){

    const errors = usernameVerify({},values);
    console.log(errors,'show error')

    passwordVerify(errors,values); 

    emailVerify(errors,values);

    return errors;
}






/** validate email */

function emailVerify(error ={}, values){

    if(!values.email){

        error.email = toast.error("Email Required...!");

    }else if(values.email.includes(" ")){

        error.email = toast.error("Wrong Email...!")

    }else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)){

        error.email = toast.error("Invalid email address...!")

    }

    return error;
}



/**  validate for the profile page */

export async function profileValidation(values){

    const errors = emailVerify([],values);

    console.log(errors);

    return errors;
}
